import React, { Component } from 'react';
import './App.css';
import { load_google_maps } from './utils.js';
import { load_places } from './utils.js';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      query: ''
    }
  }

  componentDidMount() {
    let googleMapsPromise = load_google_maps();
    let placesPromise = load_places();

    Promise.all([
      googleMapsPromise,
      placesPromise
    ])
    .then(values => {
      let google = values[0];
      this.venues = values[1].response.venues;

      this.google = values
      this.markers = [];
      this.map = new google.maps.Map(document.getElementById('map'), {
         center: {lat: 40.440624, lng: -79.995888},
         zoom: 10
       });
      this.infoWindow = new google.maps.InfoWindow();

      this.venues.forEach(venue => {
        let marker = new google.maps.Marker({
           animation: window.google.maps.Animation.DROP,
           position: {lat: venue.location.lat , lng: venue.location.lng},
           map: this.map,
           venue: venue,
           id: venue.id,
           name: venue.name
         });
         //animate markers when clicked
         marker.addListener('click', () => {
           if (marker.getAnimation() !== null) {
             marker.setAnimation(null);
           } else {
             marker.setAnimation(google.maps.Animation.BOUNCE);
           }
           //stop animation after 500
           setTimeout(() => { marker.setAnimation(null) }, 500);
         });

           google.maps.event.addListener(marker, 'click', () => {
             this.infoWindow.setContent(marker.name);
             this.map.setCenter(marker.position);
             this.infoWindow.open(this.map, marker);
            // this.map.panBy(0, -125);
           });

         this.markers.push(marker);
      });

      this.setState({ filteredVenues: this.venues });
    });
  }

listItemClick = (venue) => {
  let marker  = this.markers.filter(m => m.id === venue.id)[0];
  this.infoWindow.setContent(marker.name);
  this.map.setCenter(marker.position);
  this.infoWindow.open(this.map, marker);
  this.map.setZoom(15);
//  this.map.panBy(0, -125);
  if (marker.getAnimation() !== null) { marker.setAnimation(null) }
  else { marker.setAnimation(window.google.maps.Animation.BOUNCE); }
    //stop animation after 500
    setTimeout(() => { marker.setAnimation(null) }, 500);
  }


  filterVenues(query) {
    //loop through markers and check if query matches input
    let venueFilter = this.venues.filter(venue => venue.name.toLowerCase().includes(query.toLowerCase()));


    //toLowerCase so user search case doesn't affect query
    this.markers.forEach(marker => {
      marker.name.toLowerCase().includes(query.toLowerCase()) === true ?
      marker.setVisible(true) :
      marker.setVisible(false);
    });

    this.setState({ filteredVenues: venueFilter, query });
  }

  render() {
    return (
    <main>
      <div id="map">

      </div>
      <div id="sidebar">
        <input placeholder="search" value={this.state.query} onChange={(e) => { this.filterVenues(e.target.value) }}/>
      <br/>

      {
        this.state.filteredVenues && this.state.filteredVenues.length > 0 &&
        this.state.filteredVenues.map((venue, index) => (
          <div key={index} className="venue-item" onClick={() => { this.listItemClick(venue) }}>
          {venue.name}
          </div>
        ))
      }

      </div>
    </main>
  );
  }
}

export default App;
