import React, { Component } from 'react';
import '../App.css';

class Sidescreen extends Component {

    render() {
        const { filteredVenues, filterVenues, listItemClick, query } = this.props;
        return (
          <div id="sidebar">
            <input
                placeholder="search by name"
                value={ query }
                onChange={(e) => { this.state.props.filterVenues(e.target.value) }}
            />
          <br/>

          {
            filteredVenues && filteredVenues.length > 0 &&
            filteredVenues.map((venue, index) => (
              <div key={index} className="venue-item" onClick={() => { listItemClick(venue) }}>
              {venue.name}
              </div>
          ))
          }

          </div>
        )
}
}
export default Sidescreen;
